<?php//// Конттроллер страниц.//class C_News extends C_Base{	//	// Конструктор.	//	public function __construct(){		parent::__construct();	}		public function before(){		//$this->needLogin = true; // раскоментируйте, чтобы закрыть доступ ко всем страницам данного контроллера		parent::before();	}	public function action_index(){		$this->action_all();	}		public function action_all(){		$this->title .= 'Новости';			$art_num = isset($this->params[2]) ? (int)$this->params[2] : 1;		$mPagination = new M_Pagination('articles', '/news/all/');		$news = $mPagination->on_page(4)->page_num($art_num)->where("is_show = '1'")->all();        foreach($news as $key=>$new){            $news[$key]['intro'] = M_Articles::Instance()->intro($new['content'], 250, true);        }        		$navbar = $this->Template('v/v_navbar.php', array('navparams' => $mPagination->navparams()));		$this->content = $this->Template('v/v_news.php', array('news' => $news,																	   'navparams' => $mPagination->navparams(),																	   'navbar' => $navbar));	}	    public function action_post(){        $this->title .= 'Новость';                              $id_article = (int)$this->params[2];        $article = M_Articles::Instance()->get($id_article, true);                if(empty($article))            $this->p404();                $this->content = $this->Template('v/articles/v_post.php',  array('article' => $article,                                                                         'news' => $this->request('widget/latest_news/'.$id_article)));    }}