<?php//// Конттроллер корзины//class C_Trash extends C_Admin_Base{	private $_model; // класс модели удаленного объекта	public function Go($action, $params)	{		$this->title = $params[1].':: Удалённые объекты';		$mname = 'M_'.ucfirst($params[1]);				if (class_exists($mname ))			$this->_model = call_user_func(array($mname, 'Instance'));		else return false;		$table = $this->_model->get_table();		$page_num = isset($params[2]) ? (int)$params[2] : 1;  		// создание объекта постраничной навигации (обязательные параметры)		$mPagination = new M_Pagination($table, "trash/$table/");		// задание свойств объекта		$trash_pages = $mPagination->on_page(5)->page_num($page_num)->trash();		  		// получение html-кода панели навигации		$navbar = $this->Template('v/v_navbar.php', array('navparams' => $mPagination->navparams()));		 // формирование контента страницы  		$this->content = $this->Template('v/trash/v_trash_pages.php',   			array('table'=>	$table,  				'pk' => $this->_model->get_pk(),  				'trash_pages' => $trash_pages,                'navparams' => $mPagination->navparams(),                'navbar' => $navbar)); 		$this->render();	}	}