<?phpclass M_Templates extends M_Model{	private static $instance;	// экземпляр класса	public static function Instance()	{		if (self::$instance == null)			self::$instance = new self();					return self::$instance;	}		public function __construct(){		parent::__construct('templates', 'id_template');	}		public function get_templates(){		/*$files = glob('v/*.php');		return $files;*/		$files = array();		$scan = scandir('v');		foreach($scan as $file)		{		    if (!is_dir("v/$file"))		    {		        $files[] = $file;		    }		}		return $files;	}}