<? extract($navparams); ?><div>	<h2>Список новостей (<?=$count?>)</h2>	<table class="table table-hover table-bordered">	<thead>	<tr>		<th class="numberlist">№</th>		<th> Заголовок</th>		<th> Просмотреть</th>		<th> Редактировать</th>		<th> Переместить в корзину</th>		<th> Удалить</th>		<th> Статус</th>	</tr>	</thead>	<tbody>		<? 	$i = ($page_num - 1) * $on_page + 1; ?>		<? foreach ($articles as $article): ?>			<tr>			<? $id=$article['id_article'] ?>				<td><?=$i?></td>				<td><?=$article['title'] ?></td>				<td><a href="/news/post/<?=$article['id_article'] ?>" target="_blanc"> Просмотреть</a></td>				<td><a href="/articles/edit/<?=$id ?>"> Редактировать</a></td>                <td><? if($article['is_show']==0 || $article['is_show']==1): ;?>                     <a href="/articles/remove/<?=$id; ?>"> Переместить в корзину</a>                     <? endif; ?>                </td>				<? if(M_Helpers::can_look('ALL')): ?>				<td><a href="/articles/delete/<?=$id ?>" onClick="javascript: return confirm('Вы действительно хотите удалить?')"> Удалить</a></td>				<? endif; ?>				<td>                 <? if ($article['is_show'] == 1)                     echo "Опубликована";                   else if($article['is_show'] == 0)                    echo "Не опубликована";                   else if($article['is_show'] == 2)                     echo "Перемещена в корзину";                ?>				</td>			</tr>		<? $i++; endforeach ?>	</tbody>	</table>	<?=$navbar ?>	<br/>	<p><a class="btn btn-primary btn" href="/articles/add/">Добавить новую статью &raquo;</a></p></div>