<?php/** * Основной шаблон * =============== * $title - заголовок * $content - HTML страницы */?><!DOCTYPE html><html class="no-js" lang="ru">  <head>	<base href="<?=BASE_URL?>" />	<title><?=$title?></title>	<meta charset="utf-8" />	<? foreach($styles as $style): ?>		<link rel="stylesheet" type="text/css" media="screen" href="/<?=CSS_DIR . $style?>.css" /> 		<? endforeach; ?>	<? foreach($scripts as $script): ?>		<script src="/<?=JS_DIR . $script?>.js"></script> 		<? endforeach; ?>		<meta name="keywords" content="<?=$keywords?>">	<meta name="description" content="<?=$description?>">	</head>  <body>        <section class="wrapper">    <? if($adminlink): ?>		<ul id="admin_left">			<li><a href="/pages">Админка</a></li>			<li><a href="/login">Выйти</a></li>		</ul>	<? endif; ?>      <header class="header">                <div class="header__logo"><a href="/">DofC</a></div>        <div class="header__contacts">          <?php echo M_Helpers::text('h_contacts');?>        </div>        <div class="header__title">        	 <h2><?php echo M_Helpers::text('h_title');?></h2><br>        	<?php echo M_Helpers::text('h_subtitle');?>        </div>        <div class="header__line">			<ul>				 <? foreach($top_menu as $item): ?>					<li <?php if($item['is_active']) echo 'class="active expanded"';?> 						<?=M_Helpers::front_widget('page', $item)?>					>						<a href="/<?=$item['full_cache_url']?>">							<span <?=M_Helpers::front_field('title_in_menu', 'input')?>><?=$item['title_in_menu']?></span>						</a> 					</li>				<? endforeach; ?>			</ul>          <form class="header__search" action="search" method="post">            <input type="text" name="search" placeholder="Поиск" />            <button type="submit">Найти</button>          </form>        </div>              </header>      <section class="main">        <section class="content">       		<?=$content?>    		</section>		<section class="sidebar">                    <nav class="sidebar__menu">            <ul class="sidebar__menu-list">				<? foreach($left_menu as $item): ?>				  <li <?php if($item['is_active']) echo 'class="active expanded"';?> 					<?=M_Helpers::front_widget('page', $item)?>				  >				 <a href="/<?=$item['full_cache_url']?>">					<span <?=M_Helpers::front_field('title_in_menu', 'input')?>><?=$item['title_in_menu']?></span>				</a> 				 <? if(count($item['children']) > 0): ?>				  <ul>					  <? foreach($item['children'] as $i): ?>						<li <?php if($i['is_active']) echo 'class="active"';?> 							<?=M_Helpers::front_widget('page', $i)?>						>						<a href="/<?=$i['full_cache_url']?>">							<span <?=M_Helpers::front_field('title_in_menu', 'input')?>><?=$i['title_in_menu']?></span>						</a>					   </li>					  <? endforeach; ?>				  </ul>				 <? endif; ?>				   </li>				 <? endforeach; ?>            </ul>          </nav>         	</section>              </section>      <footer class="footer">		<div class="footer__contacts">			<?php echo M_Helpers::text('f_contacts');?>        </div>                <div class="footer__copyright">        	<?php echo M_Helpers::text('f_copyright');?>        </div>                <div class="footer__developer">        	<?php echo M_Helpers::text('f_developer');?>        </div>      </footer></body></html>