<? extract($navparams); ?><h2>Все пользователи (<?=$count?>)</h2><table class="table table-hover table-bordered">	<thead>		<tr>			<th class="numberlist">№</th>			<th>Логин</th>			<th>Имя</th>			<th>Роль</th>			<th>Привилегия</th>			<th>Редактировать</th>			<th>Удалить</th>		</tr>	</thead>	<tbody>		<? 	$i = ($page_num - 1) * $on_page + 1;			foreach($users as $user): ?>		<tr>			<td><?=$i?></td>			<td><?=$user['login']?></td>			<td><?=$user['name']?></td>			<td><?=$user['description']?></td>			<td><?=$user['description_priv']?></td>			<td><a href="/users/edit/<?=$user['id_user']?>">Редактировать</a></td>			<td>			<? if($user['id_user'] != 1): ?>				<a href="/users/delete/<?=$user['id_user']?>" onClick="javascript: return confirm('Вы действительно хотите удалить?')">Удалить</a>			<? endif; ?>			</td>		</tr>		<? $i++; endforeach ?>	</tbody></table><?=$navbar ?><br/><p><a class="btn btn-primary btn" href="/users/add/">Добавить нового пользователя &raquo;</a></p>