<div class="slider"><div class="slider__inner">    <div class="slider__crop cycle-slideshow"      data-cycle-slides="> div"      data-cycle-timeout="3000"      data-cycle-prev=".slider__prev"      data-cycle-next=".slider__next"      data-cycle-fx="fade"      data-cycle-carousel-fluid="true"      data-allow-wrap="true"      >       <? foreach($images as $img): ?>      <div class="slider__item">        <div class="slider__item-image"><a href="<?=$img['href']?>"><img src="/<?=IMG_DIR . $img['path']?>" width="464" height="315" alt="" /></a></div>        <div class="slider__item-right">          <div class="slider__item-title"><a href="<?=$img['href']?>"><?=$img['title_image']?></a></div>          <div class="slider__item-summary"><?=$img['alt']?></div>        </div>      </div>      <? endforeach; ?>    </div>    <div class="slider__prev"><span></span></div>    <div class="slider__next"><span></span></div>  </div></div>